// Inside your Address.php model file\nclass Address extends AppModel {\n    public function generateMapUrl($id = null) {\n        if (!$id) {\n            return false; // or handle this case as appropriate\n        }\n\n        $addressData = $this->findById($id);\n        if (empty($addressData)) {\n            return false; // or handle this case as appropriate\n        }\n\n        $data = $addressData['Address'];\n        $url = "http://maps.google.com/maps?oi=map&q=";\n        $url .= $data['street_num'] . '+';\n        $url .= $data['street'] . '+';\n        $url .= $data['city'] . '+';\n        $url .= $data['province'] . '+';\n        $url .= $data['country'] . '+';\n        $url .= $data['postal_code'];\n\n        return $url;\n    }\n}