$usrID = 25;\n$string = "22,23,24,25";\n\n// Split the string into an array of numbers\n$idArray = explode(',', $string);\n\n// Use array_filter to remove the unwanted usrID\n$idArray = array_filter($idArray, function($value) use ($usrID) {\n    return (int)$value !== (int)$usrID;\n});\n\n// Implode the array back into a string\n$receivers = implode(',', $idArray);\n\necho $receivers; // Outputs: "22,23,24"