foreach ($_GET as $field => $value) {\n    if (is_array($value)) {  // Check if the value is an array\n        foreach ($value as $subValue) {  // Iterate through the nested array\n            $datarray[] = $subValue;  // Add each element to datarray\n            echo "$subValue";  // Echo each sub-value\n            echo "<br>";\n        }\n    } else {\n        $datarray[] = $value;  // If not an array, add directly\n        echo "$value";  // And echo directly\n        echo "<br>";\n    }\n}\nprint_r($datarray);  // This will now correctly show all values