<?php\n\n// Generate new pair of private and public key\n$res = openssl_pkey_new();\n\n// Extract private key from the generated key pair\nopenssl_pkey_export($res, $privateKey);\n\n// Get the public key\n$publicKeyDetails = openssl_pkey_get_details($res);\n$publicKey = $publicKeyDetails['key'];\n\n$data = "Sensitive data that needs encryption.";\n\n// Encrypt with public key\nopenssl_public_encrypt($data, $encryptedData, $publicKey);\n\n// Output encrypted data (usually you would store it somewhere)\necho "Encrypted Data: " . base64_encode($encryptedData) . "\n";\n\n// Decrypt with private key\nopenssl_private_decrypt($encryptedData, $decryptedData, $privateKey);\n\n// Show decrypted data to confirm it is the same as the original\necho "Decrypted Data: " . $decryptedData . "\n";\n?>