<?php\n// Client-side pseudo-code (PHP)\nfunction encryptData($data, $recipientPublicKey) {\n    $gpg = new gnupg();\n    $gpg->addencryptkey($recipientPublicKey);\n    return $gpg->encrypt($data);\n}\n\n$data = 'Sensitive data';\n$encryptedData = encryptData($data, 'RECIPIENT_PUB_KEY');\n\n// Send the encrypted data to server\ncurl_setopt($ch, CURLOPT_POSTFIELDS, json_encode(['data' => base64_encode($encryptedData)]));\n\n// Server-side pseudo-code (PHP)\nfunction decryptData($encryptedData) {\n    $gpg = new gnupg();\n    $gpg->adddecryptkey('SECRET_KEY', 'PASSPHRASE');\n    return $gpg->decrypt($encryptedData);\n}\n\n$jsonInput = file_get_contents('php://input');\n$data = json_decode($jsonInput, true);\n\nif (isset($data['data'])) {\n    $decryptedData = decryptData(base64_decode($data['data']));\n    // Process decrypted data...\n}\n?>