<?php\nfunction getFirstAndLastDayOfMonth($year, $month) {\n    // Create a DateTime object set to the first day of the given month and year\n    $firstDay = new DateTime("$year-$month-01");\n    \n    // Get the last day of the month by modifying the first day\n    $lastDay = clone $firstDay;\n    $lastDay->modify('last day of this month');\n    \n    // Format both dates to YYYY-MM-DD\n    $firstDayFormatted = $firstDay->format('Y-m-d');\n    $lastDayFormatted = $lastDay->format('Y-m-d');\n    \n    return [\n        'first_day' => $firstDayFormatted,\n        'last_day' => $lastDayFormatted\n    ];\n}\n\n// Example usage:\n$dates = getFirstAndLastDayOfMonth(2023, 10);\necho "First Day: " . $dates['first_day'] . "\n"; // Output: First Day: 2023-10-01\necho "Last Day: " . $dates['last_day'] . "\n";   // Output: Last Day: 2023-10-31\n?>