<?php\nsession_start();\n$filename = 'download_progress_' . session_id() . '.txt';\n\nfunction writeProgress($msg) {\n    $handle = fopen($filename, 'a');\n    fwrite($handle, "$msg\n");\n    fclose($handle);\n}\n\n// Simulate file download and progress writing\nwriteProgress('Download started');\nsleep(2);\nwriteProgress('10% done');\nsleep(2); // simulate work...\nwriteProgress('50% done');\nsleep(2);\nwriteProgress('75% done');\nsleep(2);\nwriteProgress('100% done\nFinished');\n?>