class MyClass {\n    public $value;\n\n    public function __construct($value) {\n        $this->value = $value;\n    }\n\n    // Method to convert from AnotherClass to MyClass\n    public static function fromAnotherClass(AnotherClass $anotherClassObj): self {\n        return new self($anotherClassObj->property);\n    }\n}\n\nclass AnotherClass {\n    public $property;\n\n    public function __construct($property) {\n        $this->property = $property;\n    }\n}\n\n$another_class_obj = new AnotherClass('example');\n$obj = MyClass::fromAnotherClass($another_class_obj);\n\necho $obj->value; // Outputs: example