<?php\n\nfunction checkFTPStatus($host, $username, $password, $port = 21) {\n    // Establish a connection to the FTP server.\n    $ftpConnection = ftp_connect($host, $port, 90); // Timeout after 90 seconds.\n    \n    if (!$ftpConnection) {\n        return "Failed to connect to: $host";\n    }\n    \n    // Try to login with the provided username and password.\n    $loginResult = ftp_login($ftpConnection, $username, $password);\n    \n    if (!$loginResult) {\n        ftp_close($ftpConnection); // Close the connection if login fails.\n        return "Login failed for: $host";\n    }\n    \n    // If you reach here, it means the server is alive and you're authenticated.\n    // You can also check if a specific directory exists here using ftp_chdir\n    $directory = '/';  // Specify your directory to check here.\n    if (!ftp_chdir($ftpConnection, $directory)) {\n        return "Could not navigate to specified directory.";\n    }\n    \n    // Close the FTP connection.\n    ftp_close($ftpConnection);\n    \n    return "Successfully connected and authenticated to: $host";\n}\n\n$host = 'example.com';  // Replace with your FTP server\n$username = 'your_username';\n$password = 'your_password';\n\necho checkFTPStatus($host, $username, $password);\n\n?>