<?php\n\nfunction saveXmlFromUrl($url) {\n    // Fetch the XML data from the URL\n    $xmlData = file_get_contents($url);\n\n    if ($xmlData === FALSE) {\n        die('Error fetching URL: ' . $url);\n    }\n\n    // Create a unique filename based on the URL's hash and current timestamp\n    $uniqueFileName = md5($url) . '_' . time() . '.xml';\n\n    // Define the path where you want to save the file\n    $filePath = '/path/to/save/' . $uniqueFileName;\n\n    // Save the XML data to a local file\n    $fileHandle = fopen($filePath, 'w');\n    fwrite($fileHandle, $xmlData);\n    fclose($fileHandle);\n\n    echo "XML saved to: " . $filePath;\n}\n\n// Example usage\n$url = 'http://example.com/data.xml';\nsaveXmlFromUrl($url);\n\n?>