<?php\n$apiKey = "YOUR_API_KEY"; // Replace with your actual API Key\n\n// Coordinates for Point 1 and Point 2\n$origin = "lat1,lng1";\n$destination = "lat2,lng2";\n\n$url = "https://maps.googleapis.com/maps/api/distancematrix/json?origins=$origin&destinations=$destination&key=$apiKey";\n\n$response = file_get_contents($url);\n$data = json_decode($response, true);\n\nif ($data['status'] == 'OK') {\n    $distanceText = $data['rows'][0]['elements'][0]['distance']['text'];\n    $distanceValue = $data['rows'][0]['elements'][0]['distance']['value']; // Distance in meters\n    $durationText = $data['rows'][0]['elements'][0]['duration']['text'];\n    $durationValue = $data['rows'][0]['elements'][0]['duration']['value']; // Duration in seconds\n\n    echo "Distance: $distanceText ($distanceValue meters)\n";\n    echo "Duration: $durationText ($durationValue seconds)";\n} else {\n    echo "Error: " . $data['status'];\n}\n?>