<?php\n// Hex value as a string\n$hexValue = '60f347d15798c901';\n\n// Convert hex to decimal\n$ticks = hexdec($hexValue);\n\n// Define the difference in ticks between .NET and Unix epochs\n$tickDiff = 621355968000000000; // This is the number of .NET ticks from 1/1/0001 to 1/1/1970\n\n// Convert .NET ticks (100-nanosecond intervals) to seconds\n$seconds = intval(($ticks - $tickDiff) / 10000000);\n\n// Create a DateTime object from the Unix timestamp\n$dateTime = new DateTime('@' . $seconds);\n\n// Output the formatted date and time\necho $dateTime->format('Y-m-d H:i:s');\n?>