// Example of SOAP Client calling a service\n$soap_client = new SoapClient('http://example.com/service?wsdl');\n$response = $soap_client->SomeFunction(['param1' => 'value']);\n\n// Example of REST Client calling a service using curl\n$ch = curl_init('http://example.com/api/resource');\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_HTTPHEADER, ['Accept: application/json']);\n$response = json_decode(curl_exec($ch), true);\ncurl_close($ch);