$dateHeader = $_SERVER['HTTP_DATE'] ?? '';\n\n// Define the expected date format as per RFC1123\n$rfc1123Format = 'D, d M Y H:i:s \G\M\T';\n\n// Try to parse the date header\n$parsedDate = DateTime::createFromFormat($rfc1123Format, $dateHeader);\n\nif ($parsedDate !== false && $parsedDate->format($rfc1123Format) === $dateHeader) {\n    // Check if the parsed date is within 15 minutes of the server's current time\n    $serverTime = new DateTime();\n    $timeDifference = abs(($parsedDate->getTimestamp() - $serverTime->getTimestamp()) / 60);\n\n    if ($timeDifference <= 15) {\n        echo "Date header is valid and within 15 minutes of the server's current time.";\n    } else {\n        echo "Date header is not within 15 minutes of the server's current time.";\n    }\n} else {\n    echo "Invalid date format in Date header.";\n}