<?php\nsession_start();\n\nif (!isset($_SESSION['current_date'])) {\n    $_SESSION['current_date'] = date('Y-m-d'); // Initialize to today if not set\n}\n\n$currentDate = $_SESSION['current_date'];\n\n// Handle button clicks and calculate new date\nif (isset($_GET['date_modifier'])) {\n    $modifier = $_GET['date_modifier'];\n    // Calculate the new date using strtotime\n    $newDate = strtotime("$currentDate $modifier");\n    if ($newDate !== false) { // Check if strtotime succeeded\n        $_SESSION['current_date'] = date('Y-m-d', $newDate);\n    }\n}\n\n// Display the current date\necho "Current Date: " . $_SESSION['current_date'];\n?>\n\n<!-- HTML for buttons -->\n<a href="?date_modifier=-1 day">-1 day</a>\n<a href="?date_modifier=+1 day">+1 day</a>