// Initialize arrays with X and Y points\n$xpointsArray = [10, 20, 30];\n$ypointsArray = [15, 25, 35];\n\n// Array to store all the coordinate points\n$coordinates = [];\n\n// Create a grid of coordinates using nested loops\nforeach ($xpointsArray as $xvalue) {\n    foreach ($ypointsArray as $yvalue) {\n        // Store each point in an array\n        $coordinates[] = ['x' => $xvalue, 'y' => $yvalue];\n    }\n}\n\n// Function to draw a point (simulated here)\nfunction drawPoint($image, $x, $y) {\n    // GD library function would go here: imagesetpixel or similar\n    echo "Drawing point at ($x, $y)\n"; // Example output\n}\n\n// Iterate over stored coordinates to draw them\n$image = null; // Assume this is your GD image resource\nforeach ($coordinates as $point) {\n    drawPoint($image, $point['x'], $point['y']);\n}