function expandTinyUrl($shortUrl) {\n    $apiUrl = "https://api.hypotheticalurlservice.com/expand?url=" . urlencode($shortUrl);\n    \n    // Initialize a session and set the options\n    $ch = curl_init();\n    curl_setopt($ch, CURLOPT_URL, $apiUrl);\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n\n    // Execute the request and fetch response\n    $output = curl_exec($ch);\n\n    // Check for errors\n    if ($output === FALSE) {\n        echo "cURL Error: " . curl_error($ch);\n    }\n\n    // Decode JSON response\n    $response = json_decode($output, true);\n\n    // Close the session\n    curl_close($ch);\n\n    return isset($response['long_url']) ? $response['long_url'] : null;\n}\n\n// Usage\n$shortUrl = "http://tinyurl.com/xyz";\necho expandTinyUrl($shortUrl);