<?php\n// Assume $data is an array or object containing your database information\n$data = [\n    'id' => 1,\n    'name' => 'John Doe',\n    'email' => 'john.doe@example.com'\n];\n\n// Convert the PHP data structure into a JSON string\n$jsonData = json_encode($data);\n\n// Set the content type to application/json\nheader('Content-Type: application/json');\n\n// Output the JSON string\necho $jsonData;\n?>