function isValidDate($day, $month, $year) {\n    // Check if all inputs are digits\n    if (!ctype_digit($day) || !ctype_digit($month) || !ctype_digit($year)) {\n        return false;\n    }\n\n    try {\n        // Attempt to create a DateTime object with the provided date\n        $date = new DateTime("$year-$month-$day");\n        \n        // Check that the formatted string matches the input, preventing invalid dates like "2023-02-30"\n        if ($date->format('Y-m-d') === "$year-$month-$day") {\n            return true;\n        }\n    } catch (Exception $e) {\n        // An exception would be thrown for an invalid date\n        return false;\n    }\n\n    return false;\n}\n\n// Example usage:\n$isValid = isValidDate(30, 12, 2008);\nif ($isValid) {\n    echo "Valid Date.";\n} else {\n    echo "Invalid Date.";\n}