<?php\n    // set feed URL\n    $lang = $_POST['language'];\n    $query = $_POST['query'] . "%20review";\n    switch($lang){\n        case "English":\n            $feedURL = "http://gdata.youtube.com/feeds/api/videos?q=$query&v=2&format=5&lr=en";\n            break;\n        case "French":\n            $feedURL = "http://gdata.youtube.com/feeds/api/videos?q=$query&v=2&format=5&lr=fr";\n            break;\n        case "Spanish":\n            $feedURL = "http://gdata.youtube.com/feeds/api/videos?q=$query&v=2&format=5&lr=es";\n            break;                   \n    }\n\n    // read feed into SimpleXML object\n    $sxml = simplexml_load_file($feedURL);\n\n    // get the first media entry &\n   // get nodes in media: namespace for media information\n   $media = $sxml->entry[0]->children('http://search.yahoo.com/mrss/');\n\n   // get video player URL\n   $attrs = $media->group->player->attributes();\n   $videoURL = (string)$attrs['url']; // Ensure it's cast as a string\n   $videoID = extractID($videoURL);       \n\n    echo <<<EOD \n    <object width="425" height="350" data="http://www.youtube.com/v/$videoID" type="application/x-shockwave-flash">\n        <param name="src" value="http://www.youtube.com/v/$videoID" />\n    </object>\nEOD;\n?>