$dir = './';\n$dhandle = opendir($dir);\n$old_files = array();\n\nif ($dhandle) {\n    while (false !== ($fname = readdir($dhandle))) {\n        if (($fname != '.') && ($fname != '..')) {\n            $old_files[] = "./{$fname}";\n        }\n    }\n    closedir($dhandle);\n}\n\nforeach ($old_files as $file) {\n    $new_file_name = basename(str_replace(' ', '_', $file));\n    $new_file_path = dirname($file) . '/' . $new_file_name;\n    \n    if (rename($file, $new_file_path)) {\n        echo "Renamed: $file to $new_file_path\n";\n    } else {\n        echo "Failed to rename: $file\n";\n    }\n}