function mergeArraysById($array1, $array2) {\n    // Create an associative array indexed by 'id' for quick lookup\n    $indexedArray = array_column($array2, null, 'id');\n\n    foreach ($array1 as &$item) {\n        if (isset($indexedArray[$item['id']])) {\n            $item = array_merge($item, $indexedArray[$item['id']]);\n        }\n    }\n\n    return $array1;\n}\n\n$array = [\n    ['id' => 1, 'name' => 'Cat'],\n    ['id' => 2, 'name' => 'Mouse'],\n];\n\n$array2 = [\n    ['id' => 2, 'age' => 321],\n    ['id' => 1, 'age' => 123],\n];\n\n$mergedArray = mergeArraysById($array, $array2);\nprint_r($mergedArray);