try {\n    $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_user', 'your_password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $stmt = $pdo->query("SELECT domain_name, slots_config.bid \n                          FROM slots_pid\n                          JOIN slots_config ON slots_config.bid = slots_pid.bid\n                          JOIN slots_sites ON slots_sites.aid = slots_pid.aid");\n\n    $found = false;\n    while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n        if (in_array("test", $row)) {\n            $found = true;\n            break;\n        }\n    }\n\n    echo $found ? "Match found" : "Match not found";\n\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}