$array = [\n    'abc' => ['label' => 'abc', 'weight' => 3],\n    'wsx' => ['label' => 'wsx', 'weight' => 1],\n    'qay' => ['label' => 'qay', 'weight' => 1],\n    'http://test.com' => ['label' => 'http://test.com', 'weight' => 0],\n    'Nasi1' => ['label' => 'Nasi1', 'weight' => 0],\n    'fax' => ['label' => 'fax', 'weight' => 4]\n];\n\nfunction compare_label($a, $b) {\n    return strnatcmp($a['label'], $b['label']);\n}\n\nfunction compare_weight($a, $b) {\n    if ($a['weight'] == $b['weight']) {\n        return 0;\n    }\n    return ($a['weight'] < $b['weight']) ? -1 : 1;\n}\n\n// Sort by label\nusort($array, 'compare_label');\necho "Sorted by Label:\n";\nvar_dump($array);\n\n// Reset array to original state before sorting by weight\n$array = [\n    'abc' => ['label' => 'abc', 'weight' => 3],\n    'wsx' => ['label' => 'wsx', 'weight' => 1],\n    'qay' => ['label' => 'qay', 'weight' => 1],\n    'http://test.com' => ['label' => 'http://test.com', 'weight' => 0],\n    'Nasi1' => ['label' => 'Nasi1', 'weight' => 0],\n    'fax' => ['label' => 'fax', 'weight' => 4]\n];\n\n// Sort by weight\nusort($array, 'compare_weight');\necho "Sorted by Weight:\n";\nvar_dump($array);