<?php\n\nfunction combineStoreData($storeData) {\n    $dates = [];\n    $combinedVerified = [];\n    $combinedUnverified = [];\n\n    // Collect dates from all stores\n    foreach ($storeData as $data) {\n        foreach ($data['verified'] as $entry) {\n            $date = $entry['date'];\n            if (!isset($dates[$date])) {\n                $dates[$date] = ['verified' => 0, 'unverified' => 0];\n            }\n            $dates[$date]['verified']++;\n        }\n        foreach ($data['unverified'] as $entry) {\n            $date = $entry['date'];\n            if (!isset($dates[$date])) {\n                $dates[$date] = ['verified' => 0, 'unverified' => 0];\n            }\n            $dates[$date]['unverified']++;\n        }\n    }\n\n    // Determine which dates are fully verified\n    foreach ($storeData as $data) {\n        foreach ($data['verified'] as $entry) {\n            $date = $entry['date'];\n            if ($dates[$date]['unverified'] == 0) {\n                array_push($combinedVerified, $entry);\n            } else {\n                array_push($combinedUnverified, $entry);\n            }\n        }\n    }\n\n    // Add unverified entries to the combined unverified list\n    foreach ($storeData as $data) {\n        foreach ($data['unverified'] as $entry) {\n            array_push($combinedUnverified, $entry);\n        }\n    }\n\n    return [\n        'verified' => sortArrayByDate($combinedVerified),\n        'unverified' => sortArrayByDate($combinedUnverified)\n    ];\n}\n\nfunction sortArrayByDate(array $array): array {\n    usort($array, function ($a, $b) {\n        return strcmp($a['date'], $b['date']);\n    });\n    return $array;\n}\n\n// Example usage:\n$storeA = [\n    'verified' => [\n        ['sales' => 355, 'cost' => 233, 'date' => '2008-03-01'],\n        ['sales' => 235, 'cost' => 133, 'date' => '2008-03-02']\n    ],\n    'unverified' => [\n        ['sales' => 232, 'cost' => 133, 'date' => '2008-03-03'],\n        ['sales' => 335, 'cost' => 233, 'date' => '2008-03-04']\n    ]\n];\n\n$storeB = [\n    'verified' => [\n        ['sales' => 435, 'cost' => 143, 'date' => '2008-03-01'],\n        ['sales' => 335, 'cost' => 233, 'date' => '2008-03-03'],\n    ],\n    'unverified' => [\n        ['sales' => 535, 'cost' => 243, 'date' => '2008-03-04']\n    ]\n];\n\n$combinedData = combineStoreData([$storeA, $storeB]);\nprint_r($combinedData['verified']);\nprint_r($combinedData['unverified']);\n\n?>