<?php\n// Create a blank image and add some color\n$im = imagecreatetruecolor(120, 80);\nbackgroundColor = imagecolorallocate($im, 255, 255, 255); // white background\n$textColor = imagecolorallocate($im, 0, 0, 255); // blue color for text\n\n// Fill the background with white\nimagefill($im, 0, 0, $backgroundColor);\n\n// Add some text to the image\nimagestring($im, 5, 40, 30, "Hello!", $textColor);\n\n// Output the image as a PNG and free memory allocated for it\nheader('Content-Type: image/png');\nimagepng($im);\nimagedestroy($im);\n?>