try {\n    $pdo = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n    $pdo->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Prepare and execute the first query to get the end date\n    $stmt = $pdo->prepare("SELECT enddate FROM mm_eventlist_dates WHERE id = :id");\n    $stmt->execute(['id' => $id]);\n    $row = $stmt->fetch(PDO::FETCH_ASSOC);\n\n    if ($row) {\n        $enddate = $row['enddate'];\n\n        // Prepare and execute the second query using the fetched end date\n        $nextStmt = $pdo->prepare("SELECT id FROM mm_eventlist_dates WHERE startdate = DATE_ADD(:enddate, INTERVAL 1 DAY)");\n        $nextStmt->execute(['enddate' => $enddate]);\n        $nextRow = $nextStmt->fetch(PDO::FETCH_ASSOC);\n\n        if ($nextRow) {\n            echo "Next Event ID: " . $nextRow['id'];\n        } else {\n            echo "No event found that starts the next day.";\n        }\n    } else {\n        echo "Event with ID $id not found.";\n    }\n} catch (PDOException $e) {\n    echo "Error: " . $e->getMessage();\n}