<?php\n\n// For URL-encoded data (application/x-www-form-urlencoded)\nif ($_SERVER['REQUEST_METHOD'] == 'POST' && count($_POST) > 0) {\n    echo "Received via POST: myVar = {$_POST['myVar']}\n";\n} else {\n    // For reading raw data (e.g., JSON or any other format)\n    $rawData = file_get_contents('php://input');\n    $data = json_decode($rawData, true); // If your data is JSON\n    if ($data && isset($data['myVar'])) {\n        echo "Received via php://input: myVar = {$data['myVar']}\n";\n    } else {\n        echo "No data received.\n";\n    }\n}\n?>