<?php\n// Start output buffering\nob_start();\n\nfor ($i = 0; $i < 100; $i++) {\n    echo "Iteration: $i\n";\n    // Flush the output buffer to make sure data is sent to the client\n    ob_flush();\n    flush();\n    \n    // Check if the connection was closed by the client\n    if (connection_status() != CONNECTION_NORMAL) {\n        break;\n    }\n    \n    sleep(1); // Simulate a long running process\n}\n\n// Clean output buffer and close connections\nob_end_clean();\n?>