// Controller logic (Controller.php)\nclass MainController {\n    public function showHomePage() {\n        $image = Image::get('header_image', 60, ['id' => 'header_image']);\n        $sectionText = self::defaultSectionText();\n        \n        // Pass data to the view\n        return View::make('homePageView')->with(['image' => $image, 'text' => $sectionText]);\n    }\n}\n\n// HTML template logic (homePageView.php)\n<div id="header">\n    <div class="left">\n        <img src="<?php echo htmlspecialchars($image['src']); ?>" id="<?php echo htmlspecialchars($image['id']); ?>"\n             width="<?php echo htmlspecialchars($image['width']); ?>">\n    </div>\n    <div class="right" id="header_text">\n        <?php echo nl2br(htmlspecialchars($text)); ?>\n    </div>\n</div>