// Check if 'question_id' is set in the URL and it's not empty\nif (isset($_GET['question_id']) && !empty($_GET['question_id'])) {\n    // Sanitize input\n    $questionId = intval($_GET['question_id']); // Convert to integer\n\n    // Validate the sanitized data\n    if ($questionId > 0) {\n        try {\n            // Securely execute query with parameterized statement\n            $result = pg_execute($dbconn, "query_fetch", array($questionId));\n        } catch (Exception $e) {\n            // Handle exceptions\n            error_log("Database query failed: " . $e->getMessage());\n        }\n    } else {\n        // Handle invalid question_id\n        echo "Invalid question ID provided.";\n    }\n} else {\n    // Handle missing or empty 'question_id'\n    echo "'question_id' is not set in the URL.";\n}