function removeDuplicatesByUserId($fanArray) {\n    $uniqueFans = [];\n    $seenIds = [];\n\n    foreach ($fanArray as $fan) {\n        if (!isset($seenIds[$fan->userid])) {\n            $seenIds[$fan->userid] = true;\n            $uniqueFans[] = $fan;\n        }\n    }\n\n    return $uniqueFans;\n}\n\n// Example usage:\n$fans = [\n    new FanStruct('2023-10-01', 1),\n    new FanStruct('2023-10-02', 2),\n    new FanStruct('2023-10-03', 1), // Duplicate userid\n    new FanStruct('2023-10-04', 3)\n];\n\n$uniqueFans = removeDuplicatesByUserId($fans);\n\nforeach ($uniqueFans as $fan) {\n    echo "User ID: {$fan->userid}, Date: {$fan->date}\n";\n}