use Doctrine\ORM\EntityManagerInterface;\nuse Doctrine\DBAL\Query\Expression\ExpressionBuilder;\n\n// Assuming $entityManager is already defined and $user is set properly\n$duration = 24; // in hours\n\n$reset = new PasswordReset();\n$reset->code = md5(uniqid());\n$reset->User = $user;\n\n// Use the ExpressionBuilder to create a raw SQL expression for the expiration time\n$expr = new ExpressionBuilder($entityManager->getConnection()->getExpressionBuilder());\n$reset->expires = $expr->literal("now() + interval '$duration hours'");\n\n// Save the entity\n$entityManager->persist($reset);\n$entityManager->flush();