function isFileBinary($filePath) {\n    $fileHandle = fopen($filePath, "rb");\n    if ($fileHandle === FALSE){\n        return false;\n    }\n    \n    // Read the first 2KB of the file\n    $chunk = fread($fileHandle, 2048);\n    fclose($fileHandle);\n\n    // Regular expression to check for presence of non-printable characters\n    return preg_match('~[^\x20-\x7E\t\n\r]~', $chunk) > 0;\n}\n\n$filePath = 'path/to/your/file';\nif (isFileBinary($filePath)) {\n    echo "The file is binary.";\n} else {\n    echo "The file is ASCII or text";\n}