<?php\n// Collecting data from POST request\n$name       = filter_input(INPUT_POST, 'orderName', FILTER_SANITIZE_STRING);\n$company    = filter_input(INPUT_POST, 'orderCompany', FILTER_SANITIZE_STRING);\n$email      = filter_input(INPUT_POST, 'orderEmail', FILTER_SANITIZE_EMAIL);\n$phone      = filter_input(INPUT_POST, 'orderPhone', FILTER_SANITIZE_STRING);\n\n$item1      = filter_input(INPUT_POST, 'orderItem1', FILTER_SANITIZE_STRING);\n$qty1       = filter_input(INPUT_POST, 'orderQty1', FILTER_SANITIZE_NUMBER_INT);\n\n$item2      = filter_input(INPUT_POST, 'orderItem2', FILTER_SANITIZE_STRING);\n$qty2       = filter_input(INPUT_POST, 'orderQty2', FILTER_SANITIZE_NUMBER_INT);\n\n$item3      = filter_input(INPUT_POST, 'orderItem3', FILTER_SANITIZE_STRING);\n$qty3       = filter_input(INPUT_POST, 'orderQty3', FILTER_SANITIZE_NUMBER_INT);\n\n$date = date("l, F j Y, G:i");\n\n// Constructing the email message\n$message = "Message sent: $date\n\n";\n$message .= "Name: $name\n";\n$message .= "Company: $company\n";\n$message .= "Email: $email\n";\n$message .= "Phone: $phone\n\n";\n$message .= "Order:\n";\n$message .= "$item1 \tx$qty1\n";\n$message .= "$item2 \tx$qty2\n";\n$message .= "$item3 \tx$qty3";\n\n// Setting up headers and sending the email\n$headers = "From: webmaster@company.com\r\n";\n$headers .= "Reply-To: $email\r\n";\nmail("sales@company.com", "Order", $message, $headers);\n?>