<?php\n// Change the current directory to the drupal root and define 'DRUPAL_ROOT'\nchdir('/path/to/your/drupal/root');\ndefine('DRUPAL_ROOT', getcwd());\n\nrequire_once './includes/bootstrap.inc';\n\n// Bootstrap to DRUPAL_BOOTSTRAP_FULL which allows access to user session data.\ndrupal_bootstrap(DRUPAL_BOOTSTRAP_FULL);\n\n// Check if there is a current user and print their UID\nglobal $user;\nif ($user->uid) {\n    echo "Current User ID: " . $user->uid;\n} else {\n    echo "No User Logged In.";\n}\n?>