<?php\n// Define the root directory (where index.php resides)\n$drupal_root = '/path/to/your/drupal/root';\n\n// Require Drupal's bootstrap file to initialize environment\nrequire_once $drupal_root . '/includes/bootstrap.inc';\n\n// Bootstrap Drupal to at least the full level, which gives you access to user data\ndrupal_bootstrap(DRUPAL_BOOTSTRAP_FULL);\n\n// Check if there is a current user and print their UID\n$user = \Drupal::currentUser();\nif ($user->id()) {\n    echo "Current User ID: " . $user->id();\n} else {\n    echo "No User Logged In.";\n}\n\n?>