<?php\n\n   // Initialize a CURL session\n   $ch = curl_init("http://example.com/cgi-bin/perlscript.cgi");\n\n   // Set the POST option and data\n   curl_setopt($ch, CURLOPT_POST, 1);\n   curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query(['param' => 'value']));\n\n   // Get the response from the CGI script\n   $output = curl_exec($ch);\n\n   // Close CURL session\n   curl_close($ch);\n\n   echo "Output from Perl web service: <pre>$output</pre>";\n   ?>