<?php\n\n// XML Data to be sent in POST request\n$xmlPostString = '<?xml version="1.0"?><data><item>value</item></data>';\n\n// URL to send the POST request to\n$url = 'https://example.com/api/receive';\n\n// Initialize cURL session\n$ch = curl_init($url);\n\n// Set cURL options\ncurl_setopt($ch, CURLOPT_POST, true); // Indicate POST request\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $xmlPostString); // Post fields\ncurl_setopt($ch, CURLOPT_HTTPHEADER, array('Content-Type: text/xml')); // Set content type header\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true); // Return response as string instead of printing it\ncurl_setopt($ch, CURLOPT_SSL_VERIFYPEER, true); // Verify SSL certificate\ncurl_setopt($ch, CURLOPT_SSL_VERIFYHOST, 2); // Verify SSL host\n\n// Execute the request and capture the response\n$response = curl_exec($ch);\n\nif ($response === FALSE) {\n    $error = curl_error($ch);\n    curl_close($ch);\n    die('cURL Error: ' . $error);\n}\n\n// Close cURL session\ncurl_close($ch);\n\n// Handle the response here\necho "Response: " . htmlspecialchars($response, ENT_QUOTES, 'UTF-8');\n\n?>