<?php\nfunction downloadImageUsingCurl($url, $destination) {\n    // Initialize cURL session\n    $ch = curl_init($url);\n    \n    // Set options for the cURL transfer\n    curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\n    \n    // Execute a cURL session and return the result\n    $imageData = curl_exec($ch);\n    \n    if ($imageData === false) {\n        throw new Exception("cURL error: " . curl_error($ch));\n    }\n    \n    // Close the handle\n    curl_close($ch);\n    \n    // Write the contents to disk\n    if (file_put_contents($destination, $imageData) === FALSE) {\n        throw new Exception("Could not write image data to file at $destination.");\n    }\n}\n\n$remoteUrl = 'http://www.mydomain.com/myimage.jpg';\n$localPath = '/path/to/local/folder/image.jpg';\n\ntry {\n    downloadImageUsingCurl($remoteUrl, $localPath);\n    echo "Image downloaded successfully!";\n} catch (Exception $e) {\n    echo "An error occurred: " . $e->getMessage();\n}\n?>