<?php\nfunction downloadImageUsingFileGetContents($url, $destination) {\n    // Get the file contents from the URL\n    $imageData = file_get_contents($url);\n    \n    if ($imageData === FALSE) {\n        throw new Exception("Could not retrieve image from $url.");\n    }\n    \n    // Write the contents to disk\n    if (file_put_contents($destination, $imageData) === FALSE) {\n        throw new Exception("Could not write image data to file at $destination.");\n    }\n}\n\n$remoteUrl = 'http://www.mydomain.com/myimage.jpg';\n$localPath = '/path/to/local/folder/image.jpg';\n\ntry {\n    downloadImageUsingFileGetContents($remoteUrl, $localPath);\n    echo "Image downloaded successfully!";\n} catch (Exception $e) {\n    echo "An error occurred: " . $e->getMessage();\n}\n?>