global $wpdb;\n// Assuming the table prefix is not customized and is wp_\n$table_name = $wpdb->prefix . 'users';\n\n$columns_to_check = ['columnA', 'ColumnB'];\n$query = "SELECT COUNT(COLUMN_NAME) FROM INFORMATION_SCHEMA.COLUMNS WHERE TABLE_NAME = %s AND COLUMN_NAME IN (". implode(', ', array_fill(0, count($columns_to_check), '%s')) . ")";\n\n$num_columns_exist = $wpdb->get_var($wpdb->prepare($query, $table_name, ...$columns_to_check));\n\nif ($num_columns_exist < count($columns_to_check)) {\n    // Columns are missing, proceed with altering the table\n    // Example: Add columnA if it does not exist\n    if (!in_array('columnA', $columns_to_check)) {\n        $wpdb->query("ALTER TABLE {$table_name} ADD COLUMN IF NOT EXISTS columnA VARCHAR(255) DEFAULT ''");\n    }\n}