class Name {\n    private $jobs2do;\n\n    public function __construct($string) {\n        // Initialize $jobs2do as an array if not already initialized\n        if (!is_array($this->jobs2do)) {\n            $this->jobs2do = [];\n        }\n        \n        // Add the method to the array as a callable\n        $this->jobs2do[] = [$this, 'do'];\n    }\n\n    public function do() {\n        echo "Doing something!\n";\n    }\n}\n\n// Create an instance of Name\n$instance = new Name();\n\n// Execute each job in jobs2do\nforeach ($instance->jobs2do as $job) {\n    call_user_func($job);\n}