<?php\n   $moviePath = '/path/to/your/movie.mov';\n   $thumbnailPath = '/path/to/thumbnail.jpg';\n\n   // Extract thumbnail at 1 minute mark, adjust '00:01:00' as needed for desired frame time\n   exec("ffmpeg -i {$moviePath} -ss 00:01:00 -vframes 1 {$thumbnailPath} 2>&1", $output, $returnVar);\n\n   if ($returnVar === 0) {\n       echo "Thumbnail generated successfully.";\n   } else {\n       echo "Failed to generate thumbnail. Error output:\n";\n       print_r($output);\n   }\n   ?>