<?php\n$servername = "your_servername";\n$username = "your_username";\n$password = "your_password";\n\ntry {\n    $conn = new PDO("mysql:host=$servername;", $username, $password);\n    // set the PDO error mode to exception\n    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    $stmt = $conn->query("SHOW GRANTS FOR CURRENT_USER");\n    $grants = $stmt->fetchAll(PDO::FETCH_COLUMN);\n\n    $canCreateDatabase = false;\n    foreach ($grants as $grant) {\n        if (strpos($grant, 'CREATE') !== false && strpos($grant, 'ON *.*') !== false) {\n            $canCreateDatabase = true;\n            break;\n        }\n    }\n\n    if ($canCreateDatabase) {\n        echo "User can create a database.";\n        // Show the "Create database" button in UI\n    } else {\n        echo "User cannot create a database.";\n        // Hide the "Create database" button in UI\n    }\n} catch(PDOException $e) {\n    echo "Connection failed: " . $e->getMessage();\n}\n$conn = null;\n?>