<?php\n   // Load the thumbnail into a GD resource\n   $thumbnail = imagecreatefromjpeg($thumbnailPath);\n\n   // Desired dimensions for resizing\n   $newWidth = 200;\n   $newHeight = 150;\n\n   // Resize the thumbnail\n   $resizedThumbnail = imagescale($thumbnail, $newWidth, $newHeight);\n\n   // Save the resized image\n   imagejpeg($resizedThumbnail, '/path/to/resized_thumbnail.jpg');\n\n   // Free up memory\n   imagedestroy($thumbnail);\n   imagedestroy($resizedThumbnail);\n\n   echo "Resized thumbnail saved successfully.";\n   ?>