<?php\nfunction score($ups, $downs) {\n    // Calculate the ups minus downs (score)\n    return $ups - $downs;\n}\n\nfunction hotness($utime, $upvotes, $downvotes) {\n    // Current time for age adjustment\n    $order = log10(max(abs(score($upvotes, $downvotes)), 1));\n    $sign = score($upvotes, $downvotes) >= 0 ? 1 : -1;\n\n    // Convert submission/posting time to hours since epoch for age factor\n    $seconds = $utime - 1134028003;\n    $hoursSinceEpoch = $seconds / 3600;\n\n    // Calculate the final hotness score\n    return round($sign * $order + $hoursSinceEpoch, 7);\n}\n\n// Example usage:\n$submissionTime = time(); // UNIX timestamp when content was submitted\n$upvotes = 150; // Number of upvotes\n$downvotes = 20; // Number of downvotes\n\necho "Hotness Score: " . hotness($submissionTime, $upvotes, $downvotes);\n?>