use Delight\Auth\Auth;\n   use PDO;\n\n   // Setup your database connection\n   $pdo = new PDO('mysql:host=localhost;dbname=your_database;charset=utf8mb4', 'username', 'password');\n\n   $auth = new Auth($pdo);\n\n   try {\n       // Register a new user\n       $userId = $auth->register(\n           "user@example.com",\n           "securePassword2023!",\n           ['username' => 'john_doe']\n       );\n   }\n   catch (\Delight\Auth\InvalidEmailException $e) {\n       die('Invalid email address');\n   }\n   catch (\Delight\Auth\DuplicateUsernameException $e) {\n       die('Username is already taken');\n   }\n   catch (\Delight\Auth\DuplicateEmailException $e) {\n       die('Email is already used');\n   }\n   catch (\Delight\Auth\TooManyRequestsException $e) {\n       die('Too many requests');\n   }\n\n   try {\n       // Log in user\n       $auth->login("user@example.com", "securePassword2023!");\n       if ($auth->isLoggedIn()) {\n           echo 'User is logged in!';\n       }\n   } catch (\Delight\Auth\InvalidEmailException $e) {\n       die('Wrong email address');\n   } catch (\Delight\Auth\InvalidPasswordException $e) {\n       die('Wrong password');\n   } catch (\Delight\Auth\TooManyRequestsException $e) {\n       die('Too many requests');\n   }\n\n   // Manage roles\n   if ($auth->isLoggedIn()) {\n       $userId = $auth->getUserId();\n       echo 'User ID: ', $userId, '<br>';\n\n       if ($auth->isRole('admin')) {\n           echo 'User is an admin';\n       }\n   }