// application/bootstrap.php\n\n   protected function _initErrorHandler()\n   {\n       $front = Zend_Controller_Front::getInstance();\n       $front->throwExceptions(false);\n       $front->registerPlugin(new Zend_Controller_Plugin_ErrorHandler(array(\n           'module'     => 'default',\n           'controller' => 'error',\n           'action'     => 'error'\n       )));\n   }