// Sample large array representing a friend list\n$friendList = [];\nfor ($i = 0; $i < 5000; $i++) {\n    $friendList[$i] = "User" . $i;\n}\n\n// Serialize the array\n$serializedFriendList = serialize($friendList);\n\n// Store in APCu cache\napcu_store('user_friend_list', $serializedFriendList, 3600); // Cache for 1 hour\n\n// Retrieve from APCu cache\n$cachedFriendList = apcu_fetch('user_friend_list');\nif ($cachedFriendList) {\n    $friendListFromCache = unserialize($cachedFriendList);\n}